import { Component, OnInit, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router, RouterModule, ActivatedRoute } from '@angular/router';
import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatStepperModule } from '@angular/material/stepper';
import { LayoutComponent } from '@shared/components/layout/layout.component';
import { LoanService } from '@core/services/loan.service';
import { LoanTypeService } from '@core/services/loan-type.service';
import { LoanApplicationRequest, EmploymentStatus } from '@core/models/loan.model';
import { LoanType } from '@core/models/loan-type.model';

@Component({
  selector: 'app-loan-apply',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    RouterModule,
    MatCardModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatButtonModule,
    MatIconModule,
    MatProgressSpinnerModule,
    MatStepperModule,
    LayoutComponent
  ],
  templateUrl: './loan-apply.component.html',
  styleUrls: ['./loan-apply.component.scss']
})
export class LoanApplyComponent implements OnInit {
  loanTypeForm!: FormGroup;
  loanDetailsForm!: FormGroup;
  employmentForm!: FormGroup;

  loanTypes = signal<LoanType[]>([]);
  selectedLoanType = signal<LoanType | null>(null);
  isLoading = signal(false);
  isSubmitting = signal(false);
  errorMessage = signal('');
  successMessage = signal('');

  employmentStatuses = Object.values(EmploymentStatus);

  constructor(
    private fb: FormBuilder,
    private loanService: LoanService,
    private loanTypeService: LoanTypeService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.initializeForms();
    this.loadLoanTypes();

    // Check for pre-selected loan type and values from query params
    this.route.queryParams.subscribe(params => {
      if (params['loanTypeId']) {
        const loanTypeId = parseInt(params['loanTypeId']);
        if (!isNaN(loanTypeId)) {
          // Set the loan type after loan types are loaded
          setTimeout(() => {
            this.loanTypeForm.patchValue({ loanTypeId });

            // Pre-fill amount and tenure if provided (from EMI calculator)
            if (params['amount']) {
              const amount = parseFloat(params['amount']);
              if (!isNaN(amount)) {
                this.loanDetailsForm.patchValue({ amount });
              }
            }
            if (params['tenureMonths']) {
              const tenure = parseInt(params['tenureMonths']);
              if (!isNaN(tenure)) {
                this.loanDetailsForm.patchValue({ tenureMonths: tenure });
              }
            }
          }, 500);
        }
      }
    });
  }

  initializeForms(): void {
    this.loanTypeForm = this.fb.group({
      loanTypeId: ['', Validators.required]
    });

    this.loanDetailsForm = this.fb.group({
      amount: ['', [Validators.required, Validators.min(1000)]],
      tenureMonths: ['', [Validators.required, Validators.min(1)]],
      purpose: ['', [Validators.required, Validators.minLength(10)]]
    });

    this.employmentForm = this.fb.group({
      employmentStatus: ['', Validators.required],
      monthlyIncome: ['', [Validators.required, Validators.min(1000)]]
    });

    this.loanTypeForm.get('loanTypeId')?.valueChanges.subscribe(loanTypeId => {
      const loanType = this.loanTypes().find(lt => lt.id === loanTypeId);
      this.selectedLoanType.set(loanType || null);
      if (loanType) {
        this.updateValidators(loanType);
      }
    });
  }

  updateValidators(loanType: LoanType): void {
    const amountControl = this.loanDetailsForm.get('amount');
    const tenureControl = this.loanDetailsForm.get('tenureMonths');

    amountControl?.setValidators([
      Validators.required,
      Validators.min(loanType.minAmount),
      Validators.max(loanType.maxAmount)
    ]);

    tenureControl?.setValidators([
      Validators.required,
      Validators.min(loanType.minTenureMonths),
      Validators.max(loanType.maxTenureMonths)
    ]);

    amountControl?.updateValueAndValidity();
    tenureControl?.updateValueAndValidity();
  }

  loadLoanTypes(): void {
    this.isLoading.set(true);
    this.loanTypeService.getActiveLoanTypes().subscribe({
      next: (response) => {
        this.isLoading.set(false);
        if (response.success) {
          this.loanTypes.set(response.data);
        }
      },
      error: (error) => {
        this.isLoading.set(false);
        this.errorMessage.set(error.message || 'Failed to load loan types');
      }
    });
  }

  onSubmit(): void {
    if (this.loanTypeForm.invalid || this.loanDetailsForm.invalid || this.employmentForm.invalid) {
      return;
    }

    this.isSubmitting.set(true);
    this.errorMessage.set('');
    this.successMessage.set('');

    const request: LoanApplicationRequest = {
      ...this.loanTypeForm.value,
      ...this.loanDetailsForm.value,
      ...this.employmentForm.value
    };

    this.loanService.submitLoanApplication(request).subscribe({
      next: (response) => {
        this.isSubmitting.set(false);
        if (response.success) {
          this.successMessage.set('Loan application submitted successfully!');
          setTimeout(() => {
            this.router.navigate(['/loans/my-loans']);
          }, 2000);
        }
      },
      error: (error) => {
        this.isSubmitting.set(false);
        this.errorMessage.set(error.message || 'Failed to submit loan application');
      }
    });
  }

  getEstimatedEmi(): number {
    const loanType = this.selectedLoanType();
    const amount = this.loanDetailsForm.get('amount')?.value;
    const tenure = this.loanDetailsForm.get('tenureMonths')?.value;

    if (!loanType || !amount || !tenure) {
      return 0;
    }

    const monthlyRate = loanType.interestRate / 12 / 100;
    const power = Math.pow(1 + monthlyRate, tenure);
    const emi = (amount * monthlyRate * power) / (power - 1);

    return Math.round(emi);
  }

  formatCurrency(amount: number): string {
    return `₹${amount.toLocaleString('en-IN')}`;
  }

  getEmploymentStatusLabel(status: EmploymentStatus): string {
    const labels: Record<EmploymentStatus, string> = {
      [EmploymentStatus.SALARIED]: 'Salaried',
      [EmploymentStatus.SELF_EMPLOYED]: 'Self Employed',
      [EmploymentStatus.BUSINESS_OWNER]: 'Business Owner',
      [EmploymentStatus.UNEMPLOYED]: 'Unemployed',
      [EmploymentStatus.RETIRED]: 'Retired'
    };
    return labels[status] || status;
  }
}
<app-layout>
  <div class="loan-apply-container">
    <div class="page-header">
      <h1>Apply for Loan</h1>
      <button mat-button routerLink="/dashboard">
        <mat-icon>arrow_back</mat-icon>
        Back to Dashboard
      </button>
    </div>

    @if (errorMessage()) {
      <mat-card class="alert error-alert">
        <mat-icon>error</mat-icon>
        <span>{{ errorMessage() }}</span>
      </mat-card>
    }

    @if (successMessage()) {
      <mat-card class="alert success-alert">
        <mat-icon>check_circle</mat-icon>
        <span>{{ successMessage() }}</span>
      </mat-card>
    }

    <mat-card class="application-card">
      <mat-stepper #stepper linear>
        <!-- Step 1: Select Loan Type -->
        <mat-step [stepControl]="loanTypeForm">
          <form [formGroup]="loanTypeForm">
            <ng-template matStepLabel>Select Loan Type</ng-template>
            <h2>Choose Your Loan Type</h2>

            @if (isLoading()) {
              <div class="loading-container">
                <mat-spinner></mat-spinner>
                <p>Loading loan types...</p>
              </div>
            } @else {
              <mat-form-field appearance="outline" class="full-width">
                <mat-label>Loan Type</mat-label>
                <mat-select formControlName="loanTypeId">
                  @for (loanType of loanTypes(); track loanType.id) {
                    <mat-option [value]="loanType.id">
                      {{ loanType.name }} ({{ formatCurrency(loanType.minAmount) }} - {{ formatCurrency(loanType.maxAmount) }})
                    </mat-option>
                  }
                </mat-select>
                <mat-error>Please select a loan type</mat-error>
              </mat-form-field>

              @if (selectedLoanType()) {
                <mat-card class="loan-type-details">
                  <h3>{{ selectedLoanType()?.name }}</h3>
                  <p class="description">{{ selectedLoanType()?.description }}</p>
                  <div class="details-grid">
                    <div class="detail-item">
                      <label>Amount Range</label>
                      <span>{{ formatCurrency(selectedLoanType()!.minAmount) }} - {{ formatCurrency(selectedLoanType()!.maxAmount) }}</span>
                    </div>
                    <div class="detail-item">
                      <label>Tenure Range</label>
                      <span>{{ selectedLoanType()!.minTenureMonths }} - {{ selectedLoanType()!.maxTenureMonths }} months</span>
                    </div>
                    <div class="detail-item">
                      <label>Interest Rate</label>
                      <span>{{ selectedLoanType()!.interestRate }}% per annum</span>
                    </div>
                  </div>
                </mat-card>
              }
            }

            <div class="button-row">
              <button mat-raised-button color="primary" matStepperNext [disabled]="loanTypeForm.invalid">
                Next
                <mat-icon>arrow_forward</mat-icon>
              </button>
            </div>
          </form>
        </mat-step>

        <!-- Step 2: Loan Details -->
        <mat-step [stepControl]="loanDetailsForm">
          <form [formGroup]="loanDetailsForm">
            <ng-template matStepLabel>Loan Details</ng-template>
            <h2>Loan Details</h2>

            <mat-form-field appearance="outline" class="full-width">
              <mat-label>Requested Amount</mat-label>
              <input matInput type="number" formControlName="amount" placeholder="Enter amount" />
              <span matPrefix>₹&nbsp;</span>
              @if (loanDetailsForm.get('amount')?.invalid && loanDetailsForm.get('amount')?.touched && selectedLoanType()) {
                <mat-error>
                  Amount must be between {{ formatCurrency(selectedLoanType()!.minAmount) }} and {{ formatCurrency(selectedLoanType()!.maxAmount) }}
                </mat-error>
              }
            </mat-form-field>

            <mat-form-field appearance="outline" class="full-width">
              <mat-label>Tenure (Months)</mat-label>
              <input matInput type="number" formControlName="tenureMonths" placeholder="Enter tenure" />
              @if (selectedLoanType()) {
                <mat-hint>{{ selectedLoanType()!.minTenureMonths }} - {{ selectedLoanType()!.maxTenureMonths }} months</mat-hint>
              }
              @if (loanDetailsForm.get('tenureMonths')?.invalid && loanDetailsForm.get('tenureMonths')?.touched && selectedLoanType()) {
                <mat-error>
                  Tenure must be between {{ selectedLoanType()!.minTenureMonths }} and {{ selectedLoanType()!.maxTenureMonths }} months
                </mat-error>
              }
            </mat-form-field>

            <mat-form-field appearance="outline" class="full-width">
              <mat-label>Purpose of Loan</mat-label>
              <textarea matInput formControlName="purpose" rows="3" placeholder="Describe the purpose of this loan"></textarea>
              <mat-hint>Minimum 10 characters</mat-hint>
              @if (loanDetailsForm.get('purpose')?.invalid && loanDetailsForm.get('purpose')?.touched) {
                <mat-error>Purpose must be at least 10 characters</mat-error>
              }
            </mat-form-field>

            @if (getEstimatedEmi() > 0) {
              <mat-card class="emi-estimate">
                <h4>Estimated EMI</h4>
                <p class="emi-amount">{{ formatCurrency(getEstimatedEmi()) }}/month</p>
                <p class="note">*This is an approximate value. Actual EMI will be calculated upon approval.</p>
              </mat-card>
            }

            <div class="button-row">
              <button mat-button matStepperPrevious>
                <mat-icon>arrow_back</mat-icon>
                Back
              </button>
              <button mat-raised-button color="primary" matStepperNext [disabled]="loanDetailsForm.invalid">
                Next
                <mat-icon>arrow_forward</mat-icon>
              </button>
            </div>
          </form>
        </mat-step>

        <!-- Step 3: Employment Details -->
        <mat-step [stepControl]="employmentForm">
          <form [formGroup]="employmentForm">
            <ng-template matStepLabel>Employment Details</ng-template>
            <h2>Employment Information</h2>

            <mat-form-field appearance="outline" class="full-width">
              <mat-label>Employment Status</mat-label>
              <mat-select formControlName="employmentStatus">
                @for (status of employmentStatuses; track status) {
                  <mat-option [value]="status">{{ getEmploymentStatusLabel(status) }}</mat-option>
                }
              </mat-select>
              <mat-error>Please select employment status</mat-error>
            </mat-form-field>

            <mat-form-field appearance="outline" class="full-width">
              <mat-label>Monthly Income</mat-label>
              <input matInput type="number" formControlName="monthlyIncome" placeholder="Enter monthly income" />
              <span matPrefix>₹&nbsp;</span>
              @if (employmentForm.get('monthlyIncome')?.invalid && employmentForm.get('monthlyIncome')?.touched) {
                <mat-error>Please enter a valid monthly income</mat-error>
              }
            </mat-form-field>

            <div class="button-row">
              <button mat-button matStepperPrevious>
                <mat-icon>arrow_back</mat-icon>
                Back
              </button>
              <button
                mat-raised-button
                color="primary"
                (click)="onSubmit()"
                [disabled]="loanTypeForm.invalid || loanDetailsForm.invalid || employmentForm.invalid || isSubmitting()">
                @if (isSubmitting()) {
                  <mat-spinner diameter="20"></mat-spinner>
                } @else {
                  <span>Submit Application</span>
                }
              </button>
            </div>
          </form>
        </mat-step>
      </mat-stepper>
    </mat-card>
  </div>
</app-layout>
