export class EmiScheduleComponent {
  constructor(route, emiService, loanService, authService, dialog) {
    this.route = route;
    this.emiService = emiService;
    this.loanService = loanService;
    this.authService = authService;
    this.dialog = dialog;
    this.emiSchedule = signal([]);
    this.paymentHistory = signal([]);
    this.loan = signal(null);
    this.isLoading = signal(true);
    this.isLoadingPayments = signal(false);
    this.displayedColumns = ['emiNumber', 'dueDate', 'principalAmount', 'interestAmount', 'totalEmi', 'principalBalance', 'status', 'actions'];
    this.paymentColumns = ['paymentDate', 'paymentAmount', 'paymentMethod', 'transactionReference', 'recordedBy'];
  }
  ngOnInit() {
    const loanId = this.route.snapshot.params['loanId'];
    this.loadLoanDetails(loanId);
    this.loadEmiSchedule(loanId);
    this.loadPaymentHistory(loanId);
  }
  loadLoanDetails(loanId) {
    this.loanService.getLoanById(loanId).subscribe({
      next: response => {
        if (response.success && response.data) {
          this.loan.set(response.data);
        }
      }
    });
  }
  loadEmiSchedule(loanId) {
    this.isLoading.set(true);
    this.emiService.getEmiSchedule(loanId).subscribe({
      next: response => {
        if (response.success && response.data) {
          this.emiSchedule.set(response.data);
        }
        this.isLoading.set(false);
      },
      error: () => {
        this.isLoading.set(false);
      }
    });
  }
  loadPaymentHistory(loanId) {
    this.isLoadingPayments.set(true);
    this.emiService.getPaymentHistory(loanId).subscribe({
      next: response => {
        if (response.success && response.data) {
          this.paymentHistory.set(response.data);
        }
        this.isLoadingPayments.set(false);
      },
      error: () => {
        this.isLoadingPayments.set(false);
      }
    });
  }
  getTotalPaid() {
    return this.emiSchedule().filter(emi => emi.status === 'PAID').length;
  }
  getTotalPending() {
    return this.emiSchedule().filter(emi => emi.status === 'PENDING').length;
  }
  getTotalOverdue() {
    return this.emiSchedule().filter(emi => emi.status === 'OVERDUE').length;
  }
  getTotalAmountPaid() {
    return this.emiSchedule().filter(emi => emi.status === 'PAID').reduce((sum, emi) => sum + Number(emi.totalEmi), 0);
  }
  getStatusBadgeClass(status) {
    const statusMap = {
      'PENDING': 'status-warning',
      'PAID': 'status-success',
      'OVERDUE': 'status-danger',
      'PARTIAL': 'status-info'
    };
    return statusMap[status] || 'status-default';
  }
