import { Component, OnInit, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, RouterModule } from '@angular/router';
import { MatTableModule } from '@angular/material/table';
import { MatCardModule } from '@angular/material/card';
import { MatChipsModule } from '@angular/material/chips';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatDialog, MatDialogModule } from '@angular/material/dialog';
import { MatTooltipModule } from '@angular/material/tooltip';
import { EmiService } from '@core/services/emi.service';
import { LoanService } from '@core/services/loan.service';
import { AuthService } from '@core/services/auth.service';
import { PageHeaderComponent } from '@shared/components/page-header/page-header.component';
import { InrCurrencyPipe } from '@shared/pipes/inr-currency.pipe';
import { StatusBadgePipe } from '@shared/pipes/status-badge.pipe';
import { EmiSchedule, EmiPayment } from '@core/models/emi.model';
import { Loan } from '@core/models/loan.model';
import { PaymentDialogComponent } from './payment-dialog.component';

@Component({
  selector: 'app-emi-schedule',
  standalone: true,
  imports: [
    CommonModule,
    RouterModule,
    MatTableModule,
    MatCardModule,
    MatChipsModule,
    MatIconModule,
    MatButtonModule,
    MatProgressSpinnerModule,
    MatDialogModule,
    MatTooltipModule,
    PageHeaderComponent,
    InrCurrencyPipe,
    StatusBadgePipe
  ],
  templateUrl: './emi-schedule.component.html',
  styleUrls: ['./emi-schedule.component.scss']
})
export class EmiScheduleComponent implements OnInit {
  emiSchedule = signal<EmiSchedule[]>([]);
  paymentHistory = signal<EmiPayment[]>([]);
  loan = signal<Loan | null>(null);
  isLoading = signal<boolean>(true);
  isLoadingPayments = signal<boolean>(false);

  displayedColumns: string[] = [
    'emiNumber',
    'dueDate',
    'principalAmount',
    'interestAmount',
    'totalEmi',
    'principalBalance',
    'status',
    'actions'
  ];

  paymentColumns: string[] = [
    'paymentDate',
    'paymentAmount',
    'paymentMethod',
    'transactionReference',
    'recordedBy'
  ];

  constructor(
    private route: ActivatedRoute,
    private emiService: EmiService,
    private loanService: LoanService,
    private authService: AuthService,
    private dialog: MatDialog
  ) {}

  ngOnInit(): void {
    const loanId = this.route.snapshot.params['loanId'];
    this.loadLoanDetails(loanId);
    this.loadEmiSchedule(loanId);
    this.loadPaymentHistory(loanId);
  }

  loadLoanDetails(loanId: number): void {
    this.loanService.getLoanById(loanId).subscribe({
      next: (response) => {
        if (response.success && response.data) {
          this.loan.set(response.data);
        }
      }
    });
  }

  loadEmiSchedule(loanId: number): void {
    this.isLoading.set(true);
    this.emiService.getEmiSchedule(loanId).subscribe({
      next: (response) => {
        if (response.success && response.data) {
          this.emiSchedule.set(response.data);
        }
        this.isLoading.set(false);
      },
      error: () => {
        this.isLoading.set(false);
      }
    });
  }

  loadPaymentHistory(loanId: number): void {
    this.isLoadingPayments.set(true);
    this.emiService.getPaymentHistory(loanId).subscribe({
      next: (response) => {
        if (response.success && response.data) {
          this.paymentHistory.set(response.data);
        }
        this.isLoadingPayments.set(false);
      },
      error: () => {
        this.isLoadingPayments.set(false);
      }
    });
  }

  getTotalPaid(): number {
    return this.emiSchedule().filter(emi => emi.status === 'PAID').length;
  }

  getTotalPending(): number {
    return this.emiSchedule().filter(emi => emi.status === 'PENDING').length;
  }

  getTotalOverdue(): number {
    return this.emiSchedule().filter(emi => emi.status === 'OVERDUE').length;
  }

  getTotalAmountPaid(): number {
    return this.emiSchedule()
      .filter(emi => emi.status === 'PAID')
      .reduce((sum, emi) => sum + Number(emi.totalEmi), 0);
  }

  getStatusBadgeClass(status: string): string {
    const statusMap: Record<string, string> = {
      'PENDING': 'status-warning',
      'PAID': 'status-success',
      'OVERDUE': 'status-danger',
      'PARTIAL': 'status-info'
    };
    return statusMap[status] || 'status-default';
  }

  canRecordPayment(): boolean {
    return this.authService.hasRole('ADMIN') || this.authService.hasRole('LOAN_OFFICER');
  }

  canPayEmi(emi: EmiSchedule): boolean {
    return this.canRecordPayment() && (emi.status === 'PENDING' || emi.status === 'OVERDUE');
  }

  openPaymentDialog(emi: EmiSchedule): void {
    const dialogRef = this.dialog.open(PaymentDialogComponent, {
      width: '600px',
      disableClose: true,
      data: { emiSchedule: emi }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        // Reload the EMI schedule and payment history after successful payment
        const loanId = this.route.snapshot.params['loanId'];
        this.loadEmiSchedule(loanId);
        this.loadPaymentHistory(loanId);
      }
    });
  }

  getPaymentMethodLabel(method: string): string {
    const methodMap: Record<string, string> = {
      'CASH': 'Cash',
      'CHEQUE': 'Cheque',
      'ONLINE_TRANSFER': 'Online Transfer',
      'UPI': 'UPI',
      'CARD': 'Card'
    };
    return methodMap[method] || method;
  }
}
<app-page-header
  [title]="'EMI Schedule - ' + (loan()?.applicationNumber || '')"
  subtitle="View complete EMI payment schedule"
  [showBackButton]="true"
  backRoute="/dashboard">
</app-page-header>

<div class="content-container">
  <!-- Summary Cards -->
  <div class="summary-cards" *ngIf="loan() as loanData">
    <mat-card class="summary-card">
      <mat-card-content>
        <div class="card-icon primary">
          <mat-icon>account_balance_wallet</mat-icon>
        </div>
        <div class="card-details">
          <p class="card-label">Loan Amount</p>
          <p class="card-value">{{ loanData.approvedAmount | inrCurrency }}</p>
        </div>
      </mat-card-content>
    </mat-card>

    <mat-card class="summary-card">
      <mat-card-content>
        <div class="card-icon success">
          <mat-icon>check_circle</mat-icon>
        </div>
        <div class="card-details">
          <p class="card-label">EMIs Paid</p>
          <p class="card-value">{{ getTotalPaid() }} / {{ emiSchedule().length }}</p>
          <p class="card-sublabel">{{ getTotalAmountPaid() | inrCurrency }}</p>
        </div>
      </mat-card-content>
    </mat-card>

    <mat-card class="summary-card">
      <mat-card-content>
        <div class="card-icon warning">
          <mat-icon>pending</mat-icon>
        </div>
        <div class="card-details">
          <p class="card-label">Pending EMIs</p>
          <p class="card-value">{{ getTotalPending() }}</p>
        </div>
      </mat-card-content>
    </mat-card>

    <mat-card class="summary-card" *ngIf="getTotalOverdue() > 0">
      <mat-card-content>
        <div class="card-icon danger">
          <mat-icon>error</mat-icon>
        </div>
        <div class="card-details">
          <p class="card-label">Overdue EMIs</p>
          <p class="card-value">{{ getTotalOverdue() }}</p>
        </div>
      </mat-card-content>
    </mat-card>
  </div>

  <!-- EMI Schedule Table -->
  <div class="table-container">
    <table mat-table [dataSource]="emiSchedule()" class="emi-table">
      <!-- EMI Number Column -->
      <ng-container matColumnDef="emiNumber">
        <th mat-header-cell *matHeaderCellDef>EMI #</th>
        <td mat-cell *matCellDef="let emi">{{ emi.installmentNumber }}</td>
      </ng-container>

      <!-- Due Date Column -->
      <ng-container matColumnDef="dueDate">
        <th mat-header-cell *matHeaderCellDef>Due Date</th>
        <td mat-cell *matCellDef="let emi">{{ emi.dueDate | date: 'dd MMM yyyy' }}</td>
      </ng-container>

      <!-- Principal Amount Column -->
      <ng-container matColumnDef="principalAmount">
        <th mat-header-cell *matHeaderCellDef>Principal</th>
        <td mat-cell *matCellDef="let emi">{{ emi.principalAmount | inrCurrency }}</td>
      </ng-container>

      <!-- Interest Amount Column -->
      <ng-container matColumnDef="interestAmount">
        <th mat-header-cell *matHeaderCellDef>Interest</th>
        <td mat-cell *matCellDef="let emi">{{ emi.interestAmount | inrCurrency }}</td>
      </ng-container>

      <!-- Total EMI Column -->
      <ng-container matColumnDef="totalEmi">
        <th mat-header-cell *matHeaderCellDef>Total EMI</th>
        <td mat-cell *matCellDef="let emi" class="amount-cell">{{ emi.totalEmi | inrCurrency }}</td>
      </ng-container>

      <!-- Principal Balance Column -->
      <ng-container matColumnDef="principalBalance">
        <th mat-header-cell *matHeaderCellDef>Balance</th>
        <td mat-cell *matCellDef="let emi">{{ emi.outstandingBalance | inrCurrency }}</td>
      </ng-container>

      <!-- Status Column -->
      <ng-container matColumnDef="status">
        <th mat-header-cell *matHeaderCellDef>Status</th>
        <td mat-cell *matCellDef="let emi">
          <mat-chip [ngClass]="getStatusBadgeClass(emi.status)">
            {{ (emi.status | statusBadge).text }}
          </mat-chip>
        </td>
      </ng-container>

      <!-- Actions Column -->
      <ng-container matColumnDef="actions">
        <th mat-header-cell *matHeaderCellDef>Actions</th>
        <td mat-cell *matCellDef="let emi">
          @if (canPayEmi(emi)) {
            <button mat-icon-button
                    color="primary"
                    matTooltip="Record Payment"
                    (click)="openPaymentDialog(emi)">
              <mat-icon>payment</mat-icon>
            </button>
          }
          @if (emi.status === 'PAID') {
            <mat-icon class="paid-icon" matTooltip="Payment Completed">check_circle</mat-icon>
          }
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"
          [class.paid-row]="row.status === 'PAID'"
          [class.overdue-row]="row.status === 'OVERDUE'">
      </tr>
    </table>

    <div *ngIf="isLoading()" class="loading-overlay">
      <mat-spinner></mat-spinner>
    </div>

    <div *ngIf="!isLoading() && emiSchedule().length === 0" class="no-data">
      <mat-icon>inbox</mat-icon>
      <p>No EMI schedule found for this loan</p>
    </div>
  </div>

  <!-- Payment History Section -->
  @if (paymentHistory().length > 0) {
    <div class="payment-history-container">
      <h2 class="section-title">
        <mat-icon>history</mat-icon>
        Payment History
      </h2>

      <div class="table-container">
        <table mat-table [dataSource]="paymentHistory()" class="payment-table">
          <!-- Payment Date Column -->
          <ng-container matColumnDef="paymentDate">
            <th mat-header-cell *matHeaderCellDef>Payment Date</th>
            <td mat-cell *matCellDef="let payment">{{ payment.paymentDate | date: 'dd MMM yyyy' }}</td>
          </ng-container>

          <!-- Payment Amount Column -->
          <ng-container matColumnDef="paymentAmount">
            <th mat-header-cell *matHeaderCellDef>Amount</th>
            <td mat-cell *matCellDef="let payment" class="amount-cell">{{ payment.paymentAmount | inrCurrency }}</td>
          </ng-container>

          <!-- Payment Method Column -->
          <ng-container matColumnDef="paymentMethod">
            <th mat-header-cell *matHeaderCellDef>Method</th>
            <td mat-cell *matCellDef="let payment">
              <mat-chip class="method-chip">{{ getPaymentMethodLabel(payment.paymentMethod) }}</mat-chip>
            </td>
          </ng-container>

          <!-- Transaction Reference Column -->
          <ng-container matColumnDef="transactionReference">
            <th mat-header-cell *matHeaderCellDef>Transaction Ref</th>
            <td mat-cell *matCellDef="let payment">{{ payment.transactionReference || '-' }}</td>
          </ng-container>

          <!-- Recorded By Column -->
          <ng-container matColumnDef="recordedBy">
            <th mat-header-cell *matHeaderCellDef>Recorded By</th>
            <td mat-cell *matCellDef="let payment">{{ payment.recordedByName }}</td>
          </ng-container>

          <tr mat-header-row *matHeaderRowDef="paymentColumns"></tr>
          <tr mat-row *matRowDef="let row; columns: paymentColumns;"></tr>
        </table>

        <div *ngIf="isLoadingPayments()" class="loading-overlay">
          <mat-spinner></mat-spinner>
        </div>
      </div>
    </div>
  }
</div>
