import { Component, OnInit, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatNativeDateModule } from '@angular/material/core';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatTabsModule } from '@angular/material/tabs';
import { MatTableModule } from '@angular/material/table';
import { PageHeaderComponent } from '@shared/components/page-header/page-header.component';
import { InrCurrencyPipe } from '@shared/pipes/inr-currency.pipe';
import { ReportsService, LoanSummaryReport, EmiCollectionReport } from '@core/services/reports.service';
import { MatSnackBar, MatSnackBarModule } from '@angular/material/snack-bar';

@Component({
  selector: 'app-reports-dashboard',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    MatCardModule,
    MatButtonModule,
    MatIconModule,
    MatDatepickerModule,
    MatFormFieldModule,
    MatInputModule,
    MatNativeDateModule,
    MatProgressSpinnerModule,
    MatTabsModule,
    MatTableModule,
    MatSnackBarModule,
    PageHeaderComponent,
    InrCurrencyPipe
  ],
  templateUrl: './reports-dashboard.component.html',
  styleUrls: ['./reports-dashboard.component.scss']
})
export class ReportsDashboardComponent implements OnInit {
  dateRangeForm: FormGroup;
  loanReport = signal<LoanSummaryReport | null>(null);
  emiReport = signal<EmiCollectionReport | null>(null);
  isLoadingLoanReport = signal<boolean>(false);
  isLoadingEmiReport = signal<boolean>(false);

  constructor(
    private fb: FormBuilder,
    private reportsService: ReportsService,
    private snackBar: MatSnackBar
  ) {
    this.dateRangeForm = this.createForm();
  }

  ngOnInit(): void {
    // Load current month reports by default
    this.loadDefaultReports();
  }

  createForm(): FormGroup {
    const today = new Date();
    const firstDayOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
    const lastDayOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0);

    return this.fb.group({
      startDate: [firstDayOfMonth, Validators.required],
      endDate: [lastDayOfMonth, Validators.required]
    });
  }

  loadDefaultReports(): void {
    this.loadLoanReport();
    this.loadEmiReport();
  }

  loadLoanReport(): void {
    if (this.dateRangeForm.invalid) {
      this.snackBar.open('Please select valid date range', 'Close', { duration: 3000 });
      return;
    }

    const { startDate, endDate } = this.dateRangeForm.value;
    const startDateStr = this.formatDate(startDate);
    const endDateStr = this.formatDate(endDate);

    this.isLoadingLoanReport.set(true);
    this.reportsService.getLoanSummaryReport(startDateStr, endDateStr).subscribe({
      next: (response) => {
        if (response.success && response.data) {
          this.loanReport.set(response.data);
        }
        this.isLoadingLoanReport.set(false);
      },
      error: (error) => {
        console.error('Error loading loan report:', error);
        this.snackBar.open('Failed to load loan report', 'Close', { duration: 3000 });
        this.isLoadingLoanReport.set(false);
      }
    });
  }

  loadEmiReport(): void {
    if (this.dateRangeForm.invalid) {
      this.snackBar.open('Please select valid date range', 'Close', { duration: 3000 });
      return;
    }

    const { startDate, endDate } = this.dateRangeForm.value;
    const startDateStr = this.formatDate(startDate);
    const endDateStr = this.formatDate(endDate);

    this.isLoadingEmiReport.set(true);
    this.reportsService.getEmiCollectionReport(startDateStr, endDateStr).subscribe({
      next: (response) => {
        if (response.success && response.data) {
          this.emiReport.set(response.data);
        }
        this.isLoadingEmiReport.set(false);
      },
      error: (error) => {
        console.error('Error loading EMI report:', error);
        this.snackBar.open('Failed to load EMI report', 'Close', { duration: 3000 });
        this.isLoadingEmiReport.set(false);
      }
    });
  }

  loadAllReports(): void {
    this.loadLoanReport();
    this.loadEmiReport();
  }

  formatDate(date: Date): string {
    return date.toISOString().split('T')[0];
  }

  setQuickRange(range: 'today' | 'week' | 'month' | 'quarter' | 'year'): void {
    const today = new Date();
    let startDate: Date;
    let endDate: Date = today;

    switch (range) {
      case 'today':
        startDate = today;
        break;
      case 'week':
        startDate = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000);
        break;
      case 'month':
        startDate = new Date(today.getFullYear(), today.getMonth(), 1);
        endDate = new Date(today.getFullYear(), today.getMonth() + 1, 0);
        break;
      case 'quarter':
        const quarter = Math.floor(today.getMonth() / 3);
        startDate = new Date(today.getFullYear(), quarter * 3, 1);
        endDate = new Date(today.getFullYear(), quarter * 3 + 3, 0);
        break;
      case 'year':
        startDate = new Date(today.getFullYear(), 0, 1);
        endDate = new Date(today.getFullYear(), 11, 31);
        break;
      default:
        startDate = today;
    }

    this.dateRangeForm.patchValue({ startDate, endDate });
    this.loadAllReports();
  }

  calculateApprovalRate(): number {
    const report = this.loanReport();
    if (!report || report.totalLoans === 0) return 0;
    return (report.approvedLoans / report.totalLoans) * 100;
  }

  calculateRejectionRate(): number {
    const report = this.loanReport();
    if (!report || report.totalLoans === 0) return 0;
    return (report.rejectedLoans / report.totalLoans) * 100;
  }

  calculateDisbursementRate(): number {
    const report = this.loanReport();
    if (!report || report.approvedLoans === 0) return 0;
    return (report.disbursedLoans / report.approvedLoans) * 100;
  }

  exportLoanReport(): void {
    const report = this.loanReport();
    if (!report) return;

    const data = [
      ['Loan Summary Report'],
      ['Period', `${report.startDate} to ${report.endDate}`],
      [''],
      ['Metric', 'Count', 'Amount'],
      ['Total Loans', report.totalLoans, this.formatCurrency(report.totalAmount)],
      ['Approved Loans', report.approvedLoans, this.formatCurrency(report.approvedAmount)],
      ['Rejected Loans', report.rejectedLoans, '-'],
      ['Pending Loans', report.pendingLoans, '-'],
      ['Disbursed Loans', report.disbursedLoans, this.formatCurrency(report.disbursedAmount)],
      [''],
      ['Approval Rate', this.calculateApprovalRate().toFixed(2) + '%', '-'],
      ['Rejection Rate', this.calculateRejectionRate().toFixed(2) + '%', '-']
    ];

    this.downloadCSV(data, 'loan-summary-report.csv');
  }

  exportEmiReport(): void {
    const report = this.emiReport();
    if (!report) return;

    const data = [
      ['EMI Collection Report'],
      ['Period', `${report.startDate} to ${report.endDate}`],
      [''],
      ['Metric', 'Count', 'Amount'],
      ['Total EMIs Due', report.totalEmisDue, this.formatCurrency(report.totalAmountDue)],
      ['EMIs Collected', report.totalEmisCollected, this.formatCurrency(report.totalAmountCollected)],
      ['EMIs Overdue', report.totalEmisOverdue, this.formatCurrency(report.totalAmountOverdue)],
      [''],
      ['Collection Rate', report.collectionRate.toFixed(2) + '%', '-']
    ];

    this.downloadCSV(data, 'emi-collection-report.csv');
  }

  private formatCurrency(amount: number): string {
    return '₹' + amount.toLocaleString('en-IN');
  }

  private downloadCSV(data: any[][], filename: string): void {
    const csvContent = data.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);

    link.setAttribute('href', url);
    link.setAttribute('download', filename);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    this.snackBar.open('Report exported successfully', 'Close', { duration: 3000 });
  }
}
<app-page-header
  title="Reports & Analytics"
  subtitle="View comprehensive reports and analytics for loans and EMI collections"
  [showBackButton]="true"
  backRoute="/dashboard">
</app-page-header>

<div class="content-container">
  <!-- Date Range Selector -->
  <mat-card class="date-range-card">
    <mat-card-header>
      <mat-card-title>
        <mat-icon>date_range</mat-icon>
        Select Report Period
      </mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <form [formGroup]="dateRangeForm" class="date-form">
        <div class="date-fields">
          <mat-form-field appearance="outline">
            <mat-label>Start Date</mat-label>
            <input matInput [matDatepicker]="startPicker" formControlName="startDate">
            <mat-datepicker-toggle matIconSuffix [for]="startPicker"></mat-datepicker-toggle>
            <mat-datepicker #startPicker></mat-datepicker>
          </mat-form-field>

          <mat-form-field appearance="outline">
            <mat-label>End Date</mat-label>
            <input matInput [matDatepicker]="endPicker" formControlName="endDate">
            <mat-datepicker-toggle matIconSuffix [for]="endPicker"></mat-datepicker-toggle>
            <mat-datepicker #endPicker></mat-datepicker>
          </mat-form-field>

          <button mat-raised-button color="primary" (click)="loadAllReports()" [disabled]="dateRangeForm.invalid">
            <mat-icon>refresh</mat-icon>
            Generate Reports
          </button>
        </div>

        <div class="quick-ranges">
          <span class="label">Quick Select:</span>
          <button mat-button (click)="setQuickRange('today')">Today</button>
          <button mat-button (click)="setQuickRange('week')">Last 7 Days</button>
          <button mat-button (click)="setQuickRange('month')">This Month</button>
          <button mat-button (click)="setQuickRange('quarter')">This Quarter</button>
          <button mat-button (click)="setQuickRange('year')">This Year</button>
        </div>
      </form>
    </mat-card-content>
  </mat-card>

  <!-- Reports Tabs -->
  <mat-tab-group class="reports-tabs">
    <!-- Loan Summary Report Tab -->
    <mat-tab>
      <ng-template mat-tab-label>
        <mat-icon>account_balance</mat-icon>
        Loan Summary
      </ng-template>

      <div class="tab-content" *ngIf="!isLoadingLoanReport()">
        <div *ngIf="loanReport(); else noLoanData">
          <!-- Summary Cards -->
          <div class="summary-grid">
            <div class="stat-card total">
              <div class="card-icon">
                <mat-icon>description</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Total Loan Applications</div>
                <div class="card-value">{{ loanReport()!.totalLoans }}</div>
                <div class="card-amount">{{ loanReport()!.totalAmount | inrCurrency }}</div>
              </div>
            </div>

            <div class="stat-card success">
              <div class="card-icon">
                <mat-icon>check_circle</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Approved Loans</div>
                <div class="card-value">{{ loanReport()!.approvedLoans }}</div>
                <div class="card-amount">{{ loanReport()!.approvedAmount | inrCurrency }}</div>
              </div>
            </div>

            <div class="stat-card warning">
              <div class="card-icon">
                <mat-icon>pending</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Pending Review</div>
                <div class="card-value">{{ loanReport()!.pendingLoans }}</div>
                <div class="card-subtitle">Awaiting decision</div>
              </div>
            </div>

            <div class="stat-card danger">
              <div class="card-icon">
                <mat-icon>cancel</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Rejected Loans</div>
                <div class="card-value">{{ loanReport()!.rejectedLoans }}</div>
                <div class="card-subtitle">{{ calculateRejectionRate().toFixed(1) }}% rejection rate</div>
              </div>
            </div>

            <div class="stat-card info">
              <div class="card-icon">
                <mat-icon>payment</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Disbursed Loans</div>
                <div class="card-value">{{ loanReport()!.disbursedLoans }}</div>
                <div class="card-amount">{{ loanReport()!.disbursedAmount | inrCurrency }}</div>
              </div>
            </div>

            <div class="stat-card accent">
              <div class="card-icon">
                <mat-icon>trending_up</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Approval Rate</div>
                <div class="card-value">{{ calculateApprovalRate().toFixed(1) }}%</div>
                <div class="card-subtitle">Success metric</div>
              </div>
            </div>
          </div>

          <!-- Detailed Table -->
          <mat-card class="details-card">
            <mat-card-header>
              <mat-card-title>Loan Summary Details</mat-card-title>
              <button mat-raised-button color="accent" (click)="exportLoanReport()">
                <mat-icon>download</mat-icon>
                Export CSV
              </button>
            </mat-card-header>
            <mat-card-content>
              <table mat-table [dataSource]="[loanReport()!]" class="details-table">
                <ng-container matColumnDef="metric">
                  <th mat-header-cell *matHeaderCellDef>Metric</th>
                  <td mat-cell *matCellDef="let report">
                    <div>Total Applications</div>
                    <div>Approved</div>
                    <div>Rejected</div>
                    <div>Pending</div>
                    <div>Disbursed</div>
                  </td>
                </ng-container>

                <ng-container matColumnDef="count">
                  <th mat-header-cell *matHeaderCellDef>Count</th>
                  <td mat-cell *matCellDef="let report">
                    <div><strong>{{ report.totalLoans }}</strong></div>
                    <div>{{ report.approvedLoans }}</div>
                    <div>{{ report.rejectedLoans }}</div>
                    <div>{{ report.pendingLoans }}</div>
                    <div>{{ report.disbursedLoans }}</div>
                  </td>
                </ng-container>

                <ng-container matColumnDef="amount">
                  <th mat-header-cell *matHeaderCellDef>Amount</th>
                  <td mat-cell *matCellDef="let report">
                    <div><strong>{{ report.totalAmount | inrCurrency }}</strong></div>
                    <div>{{ report.approvedAmount | inrCurrency }}</div>
                    <div>-</div>
                    <div>-</div>
                    <div>{{ report.disbursedAmount | inrCurrency }}</div>
                  </td>
                </ng-container>

                <ng-container matColumnDef="percentage">
                  <th mat-header-cell *matHeaderCellDef>Percentage</th>
                  <td mat-cell *matCellDef="let report">
                    <div>100%</div>
                    <div>{{ calculateApprovalRate().toFixed(1) }}%</div>
                    <div>{{ calculateRejectionRate().toFixed(1) }}%</div>
                    <div>{{ ((report.pendingLoans / report.totalLoans) * 100).toFixed(1) }}%</div>
                    <div>{{ calculateDisbursementRate().toFixed(1) }}%</div>
                  </td>
                </ng-container>

                <tr mat-header-row *matHeaderRowDef="['metric', 'count', 'amount', 'percentage']"></tr>
                <tr mat-row *matRowDef="let row; columns: ['metric', 'count', 'amount', 'percentage'];"></tr>
              </table>
            </mat-card-content>
          </mat-card>
        </div>

        <ng-template #noLoanData>
          <div class="no-data">
            <mat-icon>inbox</mat-icon>
            <p>No loan data available for the selected period</p>
            <p class="subtitle">Try selecting a different date range</p>
          </div>
        </ng-template>
      </div>

      <div class="loading-state" *ngIf="isLoadingLoanReport()">
        <mat-spinner></mat-spinner>
        <p>Loading loan report...</p>
      </div>
    </mat-tab>

    <!-- EMI Collection Report Tab -->
    <mat-tab>
      <ng-template mat-tab-label>
        <mat-icon>payments</mat-icon>
        EMI Collections
      </ng-template>

      <div class="tab-content" *ngIf="!isLoadingEmiReport()">
        <div *ngIf="emiReport(); else noEmiData">
          <!-- Summary Cards -->
          <div class="summary-grid">
            <div class="stat-card total">
              <div class="card-icon">
                <mat-icon>event</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Total EMIs Due</div>
                <div class="card-value">{{ emiReport()!.totalEmisDue }}</div>
                <div class="card-amount">{{ emiReport()!.totalAmountDue | inrCurrency }}</div>
              </div>
            </div>

            <div class="stat-card success">
              <div class="card-icon">
                <mat-icon>check_circle</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">EMIs Collected</div>
                <div class="card-value">{{ emiReport()!.totalEmisCollected }}</div>
                <div class="card-amount">{{ emiReport()!.totalAmountCollected | inrCurrency }}</div>
              </div>
            </div>

            <div class="stat-card danger">
              <div class="card-icon">
                <mat-icon>warning</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Overdue EMIs</div>
                <div class="card-value">{{ emiReport()!.totalEmisOverdue }}</div>
                <div class="card-amount">{{ emiReport()!.totalAmountOverdue | inrCurrency }}</div>
              </div>
            </div>

            <div class="stat-card accent">
              <div class="card-icon">
                <mat-icon>show_chart</mat-icon>
              </div>
              <div class="card-content">
                <div class="card-label">Collection Rate</div>
                <div class="card-value">{{ emiReport()!.collectionRate.toFixed(1) }}%</div>
                <div class="card-subtitle">Performance metric</div>
              </div>
            </div>
          </div>

          <!-- Detailed Table -->
          <mat-card class="details-card">
            <mat-card-header>
              <mat-card-title>EMI Collection Details</mat-card-title>
              <button mat-raised-button color="accent" (click)="exportEmiReport()">
                <mat-icon>download</mat-icon>
                Export CSV
              </button>
            </mat-card-header>
            <mat-card-content>
              <table mat-table [dataSource]="[emiReport()!]" class="details-table">
                <ng-container matColumnDef="metric">
                  <th mat-header-cell *matHeaderCellDef>Metric</th>
                  <td mat-cell *matCellDef="let report">
                    <div>Total Due</div>
                    <div>Collected</div>
                    <div>Overdue</div>
                    <div>Outstanding</div>
                  </td>
                </ng-container>

                <ng-container matColumnDef="count">
                  <th mat-header-cell *matHeaderCellDef>Count</th>
                  <td mat-cell *matCellDef="let report">
                    <div><strong>{{ report.totalEmisDue }}</strong></div>
                    <div>{{ report.totalEmisCollected }}</div>
                    <div>{{ report.totalEmisOverdue }}</div>
                    <div>{{ report.totalEmisDue - report.totalEmisCollected }}</div>
                  </td>
                </ng-container>

                <ng-container matColumnDef="amount">
                  <th mat-header-cell *matHeaderCellDef>Amount</th>
                  <td mat-cell *matCellDef="let report">
                    <div><strong>{{ report.totalAmountDue | inrCurrency }}</strong></div>
                    <div class="success-text">{{ report.totalAmountCollected | inrCurrency }}</div>
                    <div class="danger-text">{{ report.totalAmountOverdue | inrCurrency }}</div>
                    <div>{{ (report.totalAmountDue - report.totalAmountCollected) | inrCurrency }}</div>
                  </td>
                </ng-container>

                <ng-container matColumnDef="percentage">
                  <th mat-header-cell *matHeaderCellDef>Percentage</th>
                  <td mat-cell *matCellDef="let report">
                    <div>100%</div>
                    <div>{{ report.collectionRate.toFixed(1) }}%</div>
                    <div>{{ ((report.totalEmisOverdue / report.totalEmisDue) * 100).toFixed(1) }}%</div>
                    <div>{{ (100 - report.collectionRate).toFixed(1) }}%</div>
                  </td>
                </ng-container>

                <tr mat-header-row *matHeaderRowDef="['metric', 'count', 'amount', 'percentage']"></tr>
                <tr mat-row *matRowDef="let row; columns: ['metric', 'count', 'amount', 'percentage'];"></tr>
              </table>
            </mat-card-content>
          </mat-card>
        </div>

        <ng-template #noEmiData>
          <div class="no-data">
            <mat-icon>inbox</mat-icon>
            <p>No EMI data available for the selected period</p>
            <p class="subtitle">Try selecting a different date range</p>
          </div>
        </ng-template>
      </div>

      <div class="loading-state" *ngIf="isLoadingEmiReport()">
        <mat-spinner></mat-spinner>
        <p>Loading EMI report...</p>
      </div>
    </mat-tab>
  </mat-tab-group>
</div>
