import { Component, OnInit, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { MatTableModule } from '@angular/material/table';
import { MatCardModule } from '@angular/material/card';
import { MatChipsModule } from '@angular/material/chips';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatTabsModule } from '@angular/material/tabs';
import { MatTooltipModule } from '@angular/material/tooltip';
import { MatDialog, MatDialogModule } from '@angular/material/dialog';
import { MatSnackBar, MatSnackBarModule } from '@angular/material/snack-bar';
import { EmiService } from '@core/services/emi.service';
import { AuthService } from '@core/services/auth.service';
import { PageHeaderComponent } from '@shared/components/page-header/page-header.component';
import { InrCurrencyPipe } from '@shared/pipes/inr-currency.pipe';
import { EmiSchedule } from '@core/models/emi.model';
import { PaymentDialogComponent } from '../emi-schedule/payment-dialog.component';

interface GroupedEmiSchedule {
  loanId: number;
  loanApplicationNumber: string;
  emis: EmiSchedule[];
  totalAmount: number;
  paidAmount: number;
  pendingAmount: number;
  nextDueDate?: string;
}

@Component({
  selector: 'app-my-emi-schedule',
  standalone: true,
  imports: [
    CommonModule,
    RouterModule,
    MatTableModule,
    MatCardModule,
    MatChipsModule,
    MatIconModule,
    MatButtonModule,
    MatProgressSpinnerModule,
    MatTabsModule,
    MatTooltipModule,
    PageHeaderComponent,
    InrCurrencyPipe
  ],
  templateUrl: './my-emi-schedule.component.html',
  styleUrls: ['./my-emi-schedule.component.scss']
})
export class MyEmiScheduleComponent implements OnInit {
  allEmis = signal<EmiSchedule[]>([]);
  groupedEmis = signal<GroupedEmiSchedule[]>([]);
  pendingEmis = signal<EmiSchedule[]>([]);
  isLoading = signal<boolean>(true);
  selectedView = signal<'all' | 'pending'>('all');

  displayedColumns: string[] = [
    'installmentNumber',
    'dueDate',
    'principalAmount',
    'interestAmount',
    'totalEmi',
    'outstandingBalance',
    'status',
    'actions'
  ];

  constructor(
    private emiService: EmiService,
    private authService: AuthService
  ) {}

  ngOnInit(): void {
    this.loadMyEmiSchedule();
    this.loadPendingEmis();
  }

  loadMyEmiSchedule(): void {
    this.isLoading.set(true);
    this.emiService.getMyEmiSchedule().subscribe({
      next: (response) => {
        if (response.success && response.data) {
          this.allEmis.set(response.data);
          this.groupEmisByLoan(response.data);
        }
        this.isLoading.set(false);
      },
      error: () => {
        this.isLoading.set(false);
      }
    });
  }

  loadPendingEmis(): void {
    this.emiService.getPendingEmis().subscribe({
      next: (response) => {
        if (response.success && response.data) {
          this.pendingEmis.set(response.data);
        }
      }
    });
  }

  groupEmisByLoan(emis: EmiSchedule[]): void {
    const grouped = emis.reduce((acc, emi) => {
      const existing = acc.find(g => g.loanId === emi.loanId);

      if (existing) {
        existing.emis.push(emi);
        existing.totalAmount += emi.totalEmi;
        if (emi.status === 'PAID') {
          existing.paidAmount += emi.totalEmi;
        } else {
          existing.pendingAmount += emi.totalEmi;
          if (!existing.nextDueDate || new Date(emi.dueDate) < new Date(existing.nextDueDate)) {
            existing.nextDueDate = emi.dueDate;
          }
        }
      } else {
        acc.push({
          loanId: emi.loanId,
          loanApplicationNumber: `LOAN-${emi.loanId}`, // This should come from the API
          emis: [emi],
          totalAmount: emi.totalEmi,
          paidAmount: emi.status === 'PAID' ? emi.totalEmi : 0,
          pendingAmount: emi.status !== 'PAID' ? emi.totalEmi : 0,
          nextDueDate: emi.status !== 'PAID' ? emi.dueDate : undefined
        });
      }

      return acc;
    }, [] as GroupedEmiSchedule[]);

    // Sort EMIs within each group by installment number
    grouped.forEach(group => {
      group.emis.sort((a, b) => a.installmentNumber - b.installmentNumber);
    });

    this.groupedEmis.set(grouped);
  }

  setView(view: 'all' | 'pending'): void {
    this.selectedView.set(view);
  }

  getStatusClass(status: string): string {
    const statusMap: Record<string, string> = {
      'PENDING': 'status-pending',
      'PAID': 'status-paid',
      'OVERDUE': 'status-overdue',
      'PARTIALLY_PAID': 'status-partial'
    };
    return statusMap[status] || 'status-default';
  }

  getStatusLabel(status: string): string {
    const labelMap: Record<string, string> = {
      'PENDING': 'Pending',
      'PAID': 'Paid',
      'OVERDUE': 'Overdue',
      'PARTIALLY_PAID': 'Partially Paid'
    };
    return labelMap[status] || status;
  }

  isOverdue(emi: EmiSchedule): boolean {
    return emi.status === 'PENDING' && new Date(emi.dueDate) < new Date();
  }

  getTotalStats() {
    const grouped = this.groupedEmis();
    return {
      totalLoans: grouped.length,
      totalAmount: grouped.reduce((sum, g) => sum + g.totalAmount, 0),
      paidAmount: grouped.reduce((sum, g) => sum + g.paidAmount, 0),
      pendingAmount: grouped.reduce((sum, g) => sum + g.pendingAmount, 0),
      totalPending: this.pendingEmis().length
    };
  }
}
<app-page-header
  title="My EMI Schedule"
  subtitle="View and manage all your EMI payments across all loans"
  [showBackButton]="true"
  backRoute="/dashboard">
</app-page-header>

<div class="content-container">
  <!-- Summary Cards -->
  <div class="summary-cards" *ngIf="!isLoading()">
    <div class="summary-card total">
      <div class="card-icon">
        <mat-icon>account_balance_wallet</mat-icon>
      </div>
      <div class="card-content">
        <div class="card-label">Total Loans</div>
        <div class="card-value">{{ getTotalStats().totalLoans }}</div>
      </div>
    </div>

    <div class="summary-card success">
      <div class="card-icon">
        <mat-icon>check_circle</mat-icon>
      </div>
      <div class="card-content">
        <div class="card-label">Paid Amount</div>
        <div class="card-value">{{ getTotalStats().paidAmount | inrCurrency }}</div>
      </div>
    </div>

    <div class="summary-card pending">
      <div class="card-icon">
        <mat-icon>pending</mat-icon>
      </div>
      <div class="card-content">
        <div class="card-label">Pending EMIs</div>
        <div class="card-value">{{ getTotalStats().totalPending }}</div>
      </div>
    </div>

    <div class="summary-card warning">
      <div class="card-icon">
        <mat-icon>payments</mat-icon>
      </div>
      <div class="card-content">
        <div class="card-label">Pending Amount</div>
        <div class="card-value">{{ getTotalStats().pendingAmount | inrCurrency }}</div>
      </div>
    </div>
  </div>

  <!-- View Toggle -->
  <div class="view-toggle">
    <button mat-raised-button
            [color]="selectedView() === 'all' ? 'primary' : ''"
            (click)="setView('all')">
      <mat-icon>list</mat-icon>
      All EMIs
    </button>
    <button mat-raised-button
            [color]="selectedView() === 'pending' ? 'accent' : ''"
            (click)="setView('pending')">
      <mat-icon>pending_actions</mat-icon>
      Pending Only ({{ pendingEmis().length }})
    </button>
  </div>

  <!-- All EMIs View (Grouped by Loan) -->
  <div *ngIf="selectedView() === 'all' && !isLoading()">
    <mat-card *ngFor="let group of groupedEmis()" class="loan-card">
      <mat-card-header>
        <mat-card-title>
          <div class="loan-header">
            <div class="loan-title">
              <mat-icon>description</mat-icon>
              <span>Loan #{{ group.loanId }}</span>
            </div>
            <button mat-button color="primary" [routerLink]="['/loans', group.loanId]">
              <mat-icon>visibility</mat-icon>
              View Loan Details
            </button>
          </div>
        </mat-card-title>
        <mat-card-subtitle>
          <div class="loan-stats">
            <div class="stat">
              <span class="stat-label">Total EMIs:</span>
              <span class="stat-value">{{ group.emis.length }}</span>
            </div>
            <div class="stat">
              <span class="stat-label">Paid:</span>
              <span class="stat-value success">{{ group.paidAmount | inrCurrency }}</span>
            </div>
            <div class="stat">
              <span class="stat-label">Pending:</span>
              <span class="stat-value warning">{{ group.pendingAmount | inrCurrency }}</span>
            </div>
            <div class="stat" *ngIf="group.nextDueDate">
              <span class="stat-label">Next Due:</span>
              <span class="stat-value">{{ group.nextDueDate | date: 'dd MMM yyyy' }}</span>
            </div>
          </div>
        </mat-card-subtitle>
      </mat-card-header>

      <mat-card-content>
        <div class="table-container">
          <table mat-table [dataSource]="group.emis" class="emi-table">
            <!-- Installment Number -->
            <ng-container matColumnDef="installmentNumber">
              <th mat-header-cell *matHeaderCellDef>EMI #</th>
              <td mat-cell *matCellDef="let emi">{{ emi.installmentNumber }}</td>
            </ng-container>

            <!-- Due Date -->
            <ng-container matColumnDef="dueDate">
              <th mat-header-cell *matHeaderCellDef>Due Date</th>
              <td mat-cell *matCellDef="let emi">
                <div class="due-date" [class.overdue]="isOverdue(emi)">
                  {{ emi.dueDate | date: 'dd MMM yyyy' }}
                  <mat-icon *ngIf="isOverdue(emi)" class="overdue-icon" matTooltip="Overdue">warning</mat-icon>
                </div>
              </td>
            </ng-container>

            <!-- Principal Amount -->
            <ng-container matColumnDef="principalAmount">
              <th mat-header-cell *matHeaderCellDef>Principal</th>
              <td mat-cell *matCellDef="let emi">{{ emi.principalAmount | inrCurrency }}</td>
            </ng-container>

            <!-- Interest Amount -->
            <ng-container matColumnDef="interestAmount">
              <th mat-header-cell *matHeaderCellDef>Interest</th>
              <td mat-cell *matCellDef="let emi">{{ emi.interestAmount | inrCurrency }}</td>
            </ng-container>

            <!-- Total EMI -->
            <ng-container matColumnDef="totalEmi">
              <th mat-header-cell *matHeaderCellDef>Total EMI</th>
              <td mat-cell *matCellDef="let emi">
                <strong>{{ emi.totalEmi | inrCurrency }}</strong>
              </td>
            </ng-container>

            <!-- Outstanding Balance -->
            <ng-container matColumnDef="outstandingBalance">
              <th mat-header-cell *matHeaderCellDef>Balance</th>
              <td mat-cell *matCellDef="let emi">{{ emi.outstandingBalance | inrCurrency }}</td>
            </ng-container>

            <!-- Status -->
            <ng-container matColumnDef="status">
              <th mat-header-cell *matHeaderCellDef>Status</th>
              <td mat-cell *matCellDef="let emi">
                <mat-chip [ngClass]="getStatusClass(emi.status)">
                  {{ getStatusLabel(emi.status) }}
                </mat-chip>
              </td>
            </ng-container>

            <!-- Actions -->
            <ng-container matColumnDef="actions">
              <th mat-header-cell *matHeaderCellDef>Actions</th>
              <td mat-cell *matCellDef="let emi">
                <button mat-icon-button
                        [routerLink]="['/emis/loan', group.loanId]"
                        matTooltip="View Full Schedule">
                  <mat-icon>visibility</mat-icon>
                </button>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table>
        </div>
      </mat-card-content>
    </mat-card>

    <div *ngIf="groupedEmis().length === 0" class="no-data">
      <mat-icon>inbox</mat-icon>
      <p>No EMI schedules found</p>
      <p class="subtitle">EMI schedules are generated after loan disbursement</p>
    </div>
  </div>

  <!-- Pending EMIs View -->
  <div *ngIf="selectedView() === 'pending' && !isLoading()">
    <div class="pending-info">
      <mat-icon>info</mat-icon>
      <span>Showing all pending EMI payments that require attention</span>
    </div>

    <div class="table-container">
      <table mat-table [dataSource]="pendingEmis()" class="emi-table">
        <!-- Loan ID -->
        <ng-container matColumnDef="loanId">
          <th mat-header-cell *matHeaderCellDef>Loan #</th>
          <td mat-cell *matCellDef="let emi">{{ emi.loanId }}</td>
        </ng-container>

        <!-- Installment Number -->
        <ng-container matColumnDef="installmentNumber">
          <th mat-header-cell *matHeaderCellDef>EMI #</th>
          <td mat-cell *matCellDef="let emi">{{ emi.installmentNumber }}</td>
        </ng-container>

        <!-- Due Date -->
        <ng-container matColumnDef="dueDate">
          <th mat-header-cell *matHeaderCellDef>Due Date</th>
          <td mat-cell *matCellDef="let emi">
            <div class="due-date" [class.overdue]="isOverdue(emi)">
              {{ emi.dueDate | date: 'dd MMM yyyy' }}
              <mat-icon *ngIf="isOverdue(emi)" class="overdue-icon" matTooltip="Overdue">warning</mat-icon>
            </div>
          </td>
        </ng-container>

        <!-- Total EMI -->
        <ng-container matColumnDef="totalEmi">
          <th mat-header-cell *matHeaderCellDef>Amount Due</th>
          <td mat-cell *matCellDef="let emi">
            <strong>{{ emi.totalEmi | inrCurrency }}</strong>
          </td>
        </ng-container>

        <!-- Outstanding Balance -->
        <ng-container matColumnDef="outstandingBalance">
          <th mat-header-cell *matHeaderCellDef>Remaining Balance</th>
          <td mat-cell *matCellDef="let emi">{{ emi.outstandingBalance | inrCurrency }}</td>
        </ng-container>

        <!-- Status -->
        <ng-container matColumnDef="status">
          <th mat-header-cell *matHeaderCellDef>Status</th>
          <td mat-cell *matCellDef="let emi">
            <mat-chip [ngClass]="getStatusClass(emi.status)">
              {{ getStatusLabel(emi.status) }}
            </mat-chip>
          </td>
        </ng-container>

        <!-- Actions -->
        <ng-container matColumnDef="actions">
          <th mat-header-cell *matHeaderCellDef>Actions</th>
          <td mat-cell *matCellDef="let emi">
            <button mat-icon-button
                    [routerLink]="['/emis/loan', emi.loanId]"
                    matTooltip="View Loan EMI Schedule">
              <mat-icon>visibility</mat-icon>
            </button>
          </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="['loanId', 'installmentNumber', 'dueDate', 'totalEmi', 'outstandingBalance', 'status', 'actions']"></tr>
        <tr mat-row *matRowDef="let row; columns: ['loanId', 'installmentNumber', 'dueDate', 'totalEmi', 'outstandingBalance', 'status', 'actions'];"></tr>
      </table>
    </div>

    <div *ngIf="pendingEmis().length === 0" class="no-data success">
      <mat-icon>check_circle</mat-icon>
      <p>No pending EMI payments!</p>
      <p class="subtitle">All your EMIs are up to date</p>
    </div>
  </div>

  <!-- Loading State -->
  <div *ngIf="isLoading()" class="loading-overlay">
    <mat-spinner></mat-spinner>
    <p>Loading your EMI schedule...</p>
  </div>
</div>
